import http.client
import mimetypes
from codecs import encode

conn = http.client.HTTPSConnection("postman-echo.com")
dataList = []
boundary = 'wL36Yn8afVp8Ag7AmP8qZ0SA4n1v9T'
dataList.append(encode('--' + boundary))
dataList.append(encode('Content-Disposition: form-data; name=binary file; filename={0}'.format('dummyBinaryFile')))

fileType = mimetypes.guess_type('/Users/ankitsaini/code/postman/postman-code-generators/dummyBinaryFile')[0] or 'application/octet-stream'
dataList.append(encode('Content-Type: {}'.format(fileType)))
dataList.append(encode(''))

with open('/Users/ankitsaini/code/postman/postman-code-generators/dummyBinaryFile', 'rb') as f:
    dataList.append(f.read())
dataList.append(encode('--'+boundary+'--'))
dataList.append(encode(''))
body = b'\r\n'.join(dataList)
payload = body
headers = {
     'Content-type': 'multipart/form-data; boundary={}'.format(boundary) 
}
conn.request("POST", "/post", payload, headers)
res = conn.getresponse()
data = res.read()
print(data.decode("utf-8"))